INSERT INTO `tbl_pages` VALUES (19, 3, 2, 16, 17, 'contacts', 'Контакты', NULL, NULL, 0, 'main', 0, '<p><strong>тел. </strong>+7 (495) 543-58-39, +7 (925) 543-58-39, 8-926-207-07-70.</p>\r\n<p><strong>Skype:</strong> otido-shyba.ru</p>\r\n<p><strong>e-mail: </strong>\r\n<a href=\"mailto:info@otido-shyba.ru\">info@otido-shyba.ru</a>\r\n\r\n</p>\r\n\r\n<p><strong>Ателье &nbsp;работает:&nbsp;</strong></p>\r\n<p>Понедельник - пятница с 10-30 до 19-30, без обеда.</p>\r\n<p>По субботам и воскресеньям &nbsp;ателье &nbsp;работает&nbsp;с 11 до 19.</p>\r\n<p>По предварительной договорённости до 20-30.</p>\r\n<br>\r\n<p><strong>ПО ВОПРОСАМ ИНДИВИДУАЛЬНОГО ОБСЛУЖИВАНИЯ ЗВОНИТЬ 8-926-207-07-70, ДМИТРИЙ</strong></p>\r\n<p><strong>Проезд:</strong> г. Москва, Ленинградский пр-т 37-Б, торговый центр \"СТАРТ\", 3-й этаж, павильон № 65-Б</p>\r\nм.Динамо - м.Аэропорт, троллейбус № 12,70 или маршрутка 18 от м. Динамо-м.Полежаевская. <br> Около здания три бесплатные автостоянки ', 0, NULL, NULL, NULL, 1, '2015-12-25 11:41:44', 0, 1);
